dialog">		<div class="modal-content">			<div class="modal-header">				<button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>				<h4>Confirmation de la suppression</h4>			</div>			<div class="modal-body">				<p>Etes-vous sûr de vouloir supprimer ce livre ?</p>			</div>			<div class="modal-footer">				<a href="#" class="btn btn-default" id="confirmModalNo">Non</a>				<a href="#" class="btn btn-primary" id="confirmModalYes">Oui</a>			</div>		</div>	</div></div><script type="text/javascript">	$(document).ready(function () {				var theHREF;				$(".confirmModalLink").click(function(e) {			e.preventDefault();			theHREF = $(this).attr("href");			$("#confirmModal").modal("show");		});				$("#confirmModalNo").click(function(e) {			$("#confirmModal").modal("hide");		});				$("#confirmModalYes").click(function(e) {			window.location.href = theHREF;		});			});</script>